mapper.select:
select tec.id,su.subjectCode,su.subjectName,gr.gradeName,cl.className,te.code,te.name from TeacherClass tec
left join Subject su on tec.subId = su.id
inner join Teacher te on tec.tId = te.id
inner join Class cl on tec.classId = cl.id
inner join Grade gr on cl.gradeId = gr.id

    
        and gr.id = #{gradeId}
    
    
        and cl.id = #{classId}
    
    
        and su.id = #{subjectId}
    

order by id desc

    limit ${(currentPage - 1) * 10} , 10

select count(*) as count from TeacherClass tec
left join Subject su on tec.subId = su.id
inner join Teacher te on tec.tId = te.id
inner join Class cl on tec.classId = cl.id
inner join Grade gr on cl.gradeId = gr.id

    
        and gr.id = #{gradeId}
    
    
        and cl.id = #{classId}
    
    
        and su.id = #{subjectId}

select re.id,re.subId,re.sId,re.time,re.result,gr.gradeName,cl.className,st.code,st.name,su.subjectName from Result re inner join Student st on re.sid = st.id inner join Class cl on st.classId = cl.id inner join Grade gr on cl.gradeId = gr.id inner join Subject su on re.subId = su.id
and st.code like "%"#{code}"%"
and st.name like "%"#{name}"%"
and gr.id = #{gradeId}
and cl.id = #{classId}
and su.id = #{subjectId}
order by id desc
limit ${(currentPage - 1) * 10} , 10

select count(*) as count from Result re
inner join Student st on re.sid = st.id
inner join Class cl on st.classId = cl.id
inner join Grade gr on cl.gradeId = gr.id
inner join Subject su on re.subId=su.id

    
        and st.code like "%"#{code}"%"
    
    
        and st.name like "%"#{name}"%"
    
    
        and gr.id = #{gradeId}
    
    
        and cl.id = #{classId}
    
    
        and su.id = #{subjectId}

