inner join Class cl on st.classId = cl.id
inner join Grade gr on cl.gradeId = gr.id
inner join TeacherClass tec on tec.classId = cl.id
inner join Subject su on tec.subId = su.id

    
        and gr.id = #{gradeId}
    
    
        and cl.id = #{classId}
    
    
        and su.id = #{subjectId}
    

order by id desc

    limit ${(currentPage - 1) * 10} , 10

