select at.id,st.code,st.name,gr.gradeName,cl.className,su.subjectName,att.AttendanceType type,at.AttendanceTime time from Attendance at inner join Student st on at.sId = st.id inner join Subject su on at.subId = su.id inner join Class cl on at.classId = cl.id inner join Grade gr on cl.gradeId = gr.id inner join AttendanceType att on at.AttendanceType = att.id
and st.code like "%"#{code}"%"
and st.name like "%"#{name}"%"
and gr.id = #{gradeId}
and cl.id = #{classId}
and su.id = #{subjectId}
order by id desc
limit ${(currentPage - 1) * 10} , 10

select count(*) as count from Attendance at inner join Student st on at.sId = st.id inner join Subject su on at.subId = su.id inner join Class cl on at.classId = cl.id inner join Grade gr on cl.gradeId = gr.id inner join AttendanceType att on at.AttendanceType = att.id
and st.code like "%"#{code}"%"
and st.name like "%"#{name}"%"
and gr.id = #{gradeId}
and cl.id = #{classId}
and su.id = #{subjectId}

