mapper.update:
update Student
set classId = #{classId}
where code = #{code}

update Class
set gradeId = #{gradeId}
where classCode = #{classCode}

update Subject
set gradeId = #{gradeId}
where subjectCode = #{subjectCode}

update Attendance
set AttendanceType = #{attendanceType}
where id = #{id}

update Class
set classCode = ${classCode},
    className = ${className}
where classCode = ${classCode}

update Subject
set subjectCode = ${subjectCode},
    subjectName = ${subjectName}
where subjectCode = ${subjectCode}
